--[[ Pie ]]--

if not init then
    init = true
    
    rx,ry = getResolution()
    sinCounter = 0
    
    col = {
        {0.035,0.045,0.08},
        {1.00,1.00,1.00},
        {0.50,0.00,0.12},
        {0.01,0.33,0.50},
        {0.00,0.50,0.35}
    }
    
    
    fontNames = {
        "FiraMono",
        --"FiraMono-Bold",
        "Montserrat",
        --"Montserrat-Light",
        "Montserrat-Bold",
        "Play",
        "Play-Bold",
        "RefrigeratorDeluxe",
        --"RefrigeratorDeluxe-Light",
        "RobotoCondensed",
        "RobotoMono"
        --"RobotoMono-Bold"
    }
    
    function Rotate(point,ang,origin)
        --rotate point around origin by angle
        local vec = {}
        local o = origin or {0,0}
        vec[1] = math.cos(ang) * (point[1]-o[1]) - math.sin(ang) * (point[2]-o[2]) + o[1]
        vec[2] = math.sin(ang) * (point[1]-o[1]) + math.cos(ang) * (point[2]-o[2]) + o[2]
        return vec
        end
    
    end --init


if not loaded then
    loaded = 1
    
    cols = 52
    rows = 26
    
    pi = "3."
    
    end


setBackgroundColor(0,0,0)
l = createLayer()
l2 = createLayer()
font = loadFont("RobotoMono",16)

-- NUMBERS
local piCounter = 1
for j=1,rows do
    for i=1,cols do
        local snum = string.sub(pi,piCounter,piCounter)
        local num = 0
        if snum ~= "." then
            num = tonumber(snum)/10
            end
        
        setNextFillColor(l,num,math.sin(sinCounter+num*math.pi)/2+0.5,1-num,0.1+math.sin(sinCounter+num*math.pi)/2+0.5)
        local x = rx/cols*(i-0.7)
        local y = ry/rows*(j-0.3)
        addText(l,font,snum,x,y)
        piCounter = piCounter + 1
        end
    end


-- TITLE
local layerTitle = createLayer()
local font = loadFont('Play-Bold', 14) 
setDefaultFillColor(layerTitle, Shape_Text, 0.1, 0.1, 0.2, 1) 
addBox(layerTitle, 30, ry-64, 220, 32)
addText(layerTitle, font, "P I E . . . . . . .", 42, ry-42)


sinCounter = sinCounter + 0.05
requestAnimationFrame(1)





